<?php
        if ($_SERVER["REQUEST_METHOD"] == "POST") {
            $name = isset($_POST['username']) ? $_POST['username'] : 'N/A';
            $club = isset($_POST['option']) ? $_POST['option'] : 'N/A';
            $date = isset($_POST['date']) ? $_POST['date'] : 'N/A';

            $skills = isset($_POST['skill']) ? $_POST['skill'] : array();
            echo '<img src="data:image/jpeg;base64," alt="">';
            echo '<br>';
            echo "<h1>Thank you $name !!</h1><br> <br>";
            echo "We received your application for the $club<br><br>";
            
            if (isset($_POST['skill'])) {
                $skills = $_POST['skill'];
                $nameskill="";
                foreach ($skills as $skill) {
                    $nameskill .=$skill ." and ";
                }
                if (count($_POST['skill'])==1) {
                    echo "You is a $skill <br><br>";
                }
                else{
                    $out = "You are .$nameskill";
                    echo substr($out,0,-5)."<br><br>";
                }
            } else {
                echo "No skills selected.";
            }
            echo "You will be available $date<br>";
            
        }
?>